Create Function dbo.fnTEAMS_IsValidName(@ID uniqueidentifier, @NAME nvarchar(128))
returns bit
as
  begin
	declare @IsValid bit;
	set @IsValid = 1;
	if exists(select NAME
	            from dbo.TEAMS
	           where NAME = @NAME 
	             and DELETED   = 0
	             and (ID <> @ID or @ID is null)
	         ) begin -- then
		set @IsValid = 0;
	end -- if;
	return @IsValid;
  end
GO

