      inner join USERS
              on USERS.ID                = ACL_ROLES_USERS.USER_ID
             and USERS.DELETED           = 0
 where ACL_ROLES.DELETED = 0
  and (USERS.STATUS is null or USERS.STATUS = N'Active')

