Create View dbo.vwUSER_PREFERENCES
as
select USER_PREFERENCES.ID
     , USER_PREFERENCES.CATEGORY
     , USER_PREFERENCES.ASSIGNED_USER_ID
     , USER_PREFERENCES.DATE_MODIFIED
