     , DISPLAY_NAME  
  from vwMODULES     
 where TABLE_NAME in (select TABLE_NAME from vwSqlTablesAudited)
   and TABLE_NAME in (select ObjectName from vwSqlColumns
                       where ColumnName = 'NAME'
                          or ObjectName in('Contacts', 'Leads', 'Prospects', 'Documents')
                     )

