Create Procedure dbo.spACL_ACTIONS_InsertOnly
	( @NAME              nvarchar(150)
	, @CATEGORY          nvarchar(100)
	, @ACLTYPE           nvarchar(100)
	, @ACLACCESS         int
	)
as
  begin
	set nocount on
	
	declare @ID uniqueidentifier;
	
	-- BEGIN Oracle Exception
		select @ID = ID
		  from ACL_ACTIONS
		 where NAME     = @NAME    
		   and CATEGORY = @CATEGORY
		   and DELETED  = 0        ;
	-- END Oracle Exception

	if dbo.fnIsEmptyGuid(@ID) = 1 begin -- then
		set @ID = newid();
		insert into ACL_ACTIONS
			( ID               
			, CREATED_BY       
			, DATE_ENTERED     
			, MODIFIED_USER_ID 
			, DATE_MODIFIED    
			, NAME             
			, CATEGORY         
			, ACLTYPE          
			, ACLACCESS        
			)
		values 	( @ID               
			, null       
			,  getdate()        
			, null 
			,  getdate()        
			, @NAME             
			, @CATEGORY         
			, @ACLTYPE          
			, @ACLACCESS        
			);
	end -- if;
  end
GO
 
