Create Procedure dbo.spDETAILVIEWS_FIELDS_Update
	( @ID                uniqueidentifier output
	, @MODIFIED_USER_ID  uniqueidentifier
	, @DETAIL_NAME       nvarchar( 50)
	, @FIELD_INDEX       int
