-- 04/13/2008 Paul.  Manually insert the ID to ease migration to Oracle. 
-- 03/14/2014 Paul.  Allow hidden buttons to be created. 
Create Procedure dbo.spDYNAMIC_BUTTONS_CopyDefault
	( @SOURCE_VIEW_NAME    nvarchar(50)
	, @NEW_VIEW_NAME       nvarchar(50)
	, @MODULE_NAME         nvarchar(25)
	)
as
  begin
	set nocount on
	
	if not exists(select * from DYNAMIC_BUTTONS where VIEW_NAME = @NEW_VIEW_NAME and DELETED = 0) begin -- then
		insert into DYNAMIC_BUTTONS
			( ID
			, DATE_ENTERED      
			, DATE_MODIFIED     
			, VIEW_NAME         
			, CONTROL_INDEX     
			, CONTROL_TYPE      
			, DEFAULT_VIEW      
			, MODULE_NAME       
			, MODULE_ACCESS_TYPE
			, TARGET_NAME       
			, TARGET_ACCESS_TYPE
			, MOBILE_ONLY       
			, ADMIN_ONLY        
			, EXCLUDE_MOBILE    
			, HIDDEN            
			, CONTROL_TEXT      
			, CONTROL_TOOLTIP   
			, CONTROL_ACCESSKEY 
			, CONTROL_CSSCLASS  
			, TEXT_FIELD        
			, ARGUMENT_FIELD    
			, COMMAND_NAME      
			, URL_FORMAT        
			, URL_TARGET        
			, ONCLICK_SCRIPT    
			)
		select	   newid()
			,  getdate()
			,  getdate()
			, @NEW_VIEW_NAME
			,  CONTROL_INDEX     
			,  CONTROL_TYPE      
			,  DEFAULT_VIEW      
			, @MODULE_NAME       
			,  MODULE_ACCESS_TYPE
			,  TARGET_NAME       
			,  TARGET_ACCESS_TYPE
			,  MOBILE_ONLY       
			,  ADMIN_ONLY        
			,  EXCLUDE_MOBILE    
			,  HIDDEN            
			,  CONTROL_TEXT      
			,  CONTROL_TOOLTIP   
			,  CONTROL_ACCESSKEY 
			,  CONTROL_CSSCLASS  
			,  TEXT_FIELD        
			,  ARGUMENT_FIELD    
			,  COMMAND_NAME      
			,  URL_FORMAT        
			,  URL_TARGET        
			,  ONCLICK_SCRIPT    
		  from DYNAMIC_BUTTONS
		 where VIEW_NAME = @SOURCE_VIEW_NAME
		   and DELETED   = 0;
	end -- if;
  end
