	, @MODIFIED_USER_ID   uniqueidentifier
	, @NAME               nvarchar(60)
	, @USER_ID            uniqueidentifier
	, @FROM_NUMBER        nvarchar(100)
	)
as
  begin
	set nocount on
	
	if not exists(select * from OUTBOUND_SMS where ID = @ID) begin -- then
		if dbo.fnIsEmptyGuid(@ID) = 1 begin -- then
			set @ID = newid();
		end -- if;
		insert into OUTBOUND_SMS
			( ID                
			, CREATED_BY        
			, DATE_ENTERED      
			, MODIFIED_USER_ID  
			, DATE_MODIFIED     
			, DATE_MODIFIED_UTC 
			, NAME              
			, USER_ID           
			, FROM_NUMBER       
			)
		values 	( @ID                
			, @MODIFIED_USER_ID        
			,  getdate()         
			, @MODIFIED_USER_ID  
			,  getdate()         
			,  getutcdate()      
			, @NAME              
			, @USER_ID           
			, @FROM_NUMBER       
			);
	end else begin
		update OUTBOUND_SMS
		   set MODIFIED_USER_ID   = @MODIFIED_USER_ID  
		     , DATE_MODIFIED      =  getdate()         
		     , DATE_MODIFIED_UTC  =  getutcdate()      
		     , NAME               = @NAME              
		     , USER_ID            = @USER_ID           
		     , FROM_NUMBER        = @FROM_NUMBER       
		 where ID                 = @ID                ;
	end -- if;
  end
GO

