-- 12/31/2007 Paul.  Don't need to insert LAST_RUN. 
Create Procedure dbo.spSCHEDULERS_Update
	( @ID                uniqueidentifier output
	, @MODIFIED_USER_ID  uniqueidentifier
	, @NAME              nvarchar(255)
	, @JOB               nvarchar(255)
	, @DATE_TIME_START   datetime
	, @DATE_TIME_END     datetime
	, @JOB_INTERVAL      nvarchar(100)
	, @TIME_FROM         datetime
	, @TIME_TO           datetime
	, @STATUS            nvarchar(25)
	, @CATCH_UP          bit
	)
as
  begin
	set nocount on

	declare @VALIDATE_CRON bit;
	-- 12/31/2007 Paul.  Only update the scheduler if the CRON can be validated. 
	set @VALIDATE_CRON = dbo.fnCronRun(@JOB_INTERVAL, getdate(), 5);
	if @@ERROR = 0 begin -- then
		if not exists(select * from SCHEDULERS where ID = @ID) begin -- then
			if dbo.fnIsEmptyGuid(@ID) = 1 begin -- then
				set @ID = newid();
			end -- if;
			insert into SCHEDULERS
				( ID               
				, CREATED_BY       
				, DATE_ENTERED     
				, MODIFIED_USER_ID 
				, DATE_MODIFIED    
				, NAME             
				, JOB              
				, DATE_TIME_START  
				, DATE_TIME_END    
				, JOB_INTERVAL     
				, TIME_FROM        
				, TIME_TO          
				, STATUS           
				, CATCH_UP         
				)
			values 	( @ID               
				, @MODIFIED_USER_ID       
				,  getdate()        
				, @MODIFIED_USER_ID 
				,  getdate()        
				, @NAME             
				, @JOB              
				, @DATE_TIME_START  
				, @DATE_TIME_END    
				, @JOB_INTERVAL     
				, @TIME_FROM        
				, @TIME_TO          
				, @STATUS           
				, @CATCH_UP         
				);
		end else begin
			update SCHEDULERS
			   set MODIFIED_USER_ID  = @MODIFIED_USER_ID 
			     , DATE_MODIFIED     =  getdate()        
			     , DATE_MODIFIED_UTC =  getutcdate()     
			     , NAME              = @NAME             
			     , JOB               = @JOB              
			     , DATE_TIME_START   = @DATE_TIME_START  
			     , DATE_TIME_END     = @DATE_TIME_END    
			     , JOB_INTERVAL      = @JOB_INTERVAL     
			     , TIME_FROM         = @TIME_FROM        
			     , TIME_TO           = @TIME_TO          
			     , STATUS            = @STATUS           
			     , CATCH_UP          = @CATCH_UP         
			 where ID                = @ID               ;
		end -- if;
	end -- if;
  end
GO
 
Grant Execute on dbo.spSCHEDULERS_Update to public;
GO

