 *********************************************************************************************************************/
Create Procedure dbo.spUSERS_LAST_IMPORT_Update
	( @ID                uniqueidentifier output
	, @MODIFIED_USER_ID  uniqueidentifier
	, @ASSIGNED_USER_ID  uniqueidentifier
	, @BEAN_TYPE         nvarchar(25)
	, @BEAN_ID           uniqueidentifier
	)
as
  begin
	set nocount on
	
	if not exists(select * from USERS_LAST_IMPORT where ID = @ID) begin -- then
		if dbo.fnIsEmptyGuid(@ID) = 1 begin -- then
			set @ID = newid();
		end -- if;
		insert into USERS_LAST_IMPORT
			( ID               
			, CREATED_BY       
			, DATE_ENTERED     
			, MODIFIED_USER_ID 
			, DATE_MODIFIED    
			, ASSIGNED_USER_ID 
			, BEAN_TYPE        
			, BEAN_ID          
			)
		values
			( @ID               
			, @MODIFIED_USER_ID 
			,  getdate()        
			, @MODIFIED_USER_ID 
			,  getdate()        
			, @ASSIGNED_USER_ID 
			, @BEAN_TYPE        
			, @BEAN_ID          
			);
	end else begin
		update USERS_LAST_IMPORT
		   set MODIFIED_USER_ID  = @MODIFIED_USER_ID 
		     , DATE_MODIFIED     =  getdate()        
		     , DATE_MODIFIED_UTC =  getutcdate()     
		     , ASSIGNED_USER_ID  = @ASSIGNED_USER_ID 
		     , BEAN_TYPE         = @BEAN_TYPE        
		     , BEAN_ID           = @BEAN_ID          
		 where ID                = @ID               ;
	end -- if;
  end
GO

