			, @VIEW_NAME                   
			, @TEMP_CONTROL_INDEX          
			, N'Button'                    -- CONTROL_TYPE
			, @MODULE_NAME                 
			, N'edit'                      -- MODULE_ACCESS_TYPE
			, null                         -- TARGET_NAME         
			, null                         -- TARGET_ACCESS_TYPE  
			, N'.LBL_SAVE_CONCURRENCY_LABEL' -- CONTROL_TEXT        
