			, null                         -- URL_FORMAT
			, null                         -- URL_TARGET
			, null                         -- ONCLICK_SCRIPT      
			, null                         -- MOBILE_ONLY         
			, 0                            -- ADMIN_ONLY          
			, null                         -- EXCLUDE_MOBILE      
			, 1                            -- HIDDEN              
			;
	end -- if;
  end
