	if exists(select * from GRIDVIEWS_COLUMNS where GRID_NAME = 'AuditEvents.ListView' and DATA_FIELD = 'DATE_MODIFIED' and DATA_FORMAT is null and DELETED = 0) begin -- then
		update GRIDVIEWS_COLUMNS
