	if exists(select * from GRIDVIEWS where NAME = 'AuditEvents.ListView' and SORT_FIELD is null and DELETED = 0) begin -- then
		exec dbo.spGRIDVIEWS_UpdateSort null, 'AuditEvents.ListView', 'DATE_MODIFIED', 'desc';
	end -- if;
end -- if;
